import React, { useState, useEffect } from 'react';
import { Button, Form } from 'react-bootstrap';

export function LogIn() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const handleSubmit = (e) => {
    e.preventDefault();
    alert('que pasa chavales');
  };

  useEffect(() => console.log(password), [password]);
  useEffect(() => console.log(username), [username]);
  return (
    <div className="login">
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="Username">
          <Form.Label>Log in to an existing account</Form.Label>
          <Form.Control onChange={setUsername} placeholder="Enter Username" />
          <Form.Text className="text-muted">
            If you forgot your username go fuck yourself
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            onChange={setPassword}
            type="password"
            placeholder="Password"
          />
        </Form.Group>
        <Button type="submit">Submit</Button>
      </Form>
    </div>
  );
}
