import '../styles/LogIn.css';
import React, { useState, useEffect } from 'react';
import { Button, Form } from 'react-bootstrap';

export function LogIn() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState({ username: '', password: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username != '' && password != '') {
      setUser({ username: username, password: password });
      alert('usuario: ' + user.username + '\ncontrasenia: ' + password);

    }else if (username == ''){
      alert('pone usuario papi')
    }else if (password == ''){
      alert('pone contrasenia papi')
    } else {
      alert('llena las dos cosas gordo puto');
    }
  };

  useEffect(() => console.log(password), [password]);
  useEffect(() => console.log(username), [username]);
  return (
    <div className="login">
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="Username">
          <Form.Label>Log in to an existing account</Form.Label>
          <Form.Control
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter Username"
          />
          <Form.Text className="text-muted">
            If you forgot your username go fuck yourself
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            placeholder="Password"
          />
        </Form.Group>
        <Button style={{ 'justify-self': 'center' }} type="submit">
          Submit
        </Button>
      </Form>
    </div>
  );
}
